import './register.scss';
import { Link, useNavigate } from "react-router-dom";
import { request, setAuthToken } from '../../lib/apiRequest';
import { useState } from "react";
import * as React from 'react';

function Register() {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const onChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        if (name === "username") {
            setUsername(value);
        } else if (name === "email") {
            setEmail(value);
        } else if (name === "password") {
            setPassword(value);
        }
    };

    const onRegister = (e, username, email, password) => {
        e.preventDefault();
        setError("");
        setIsLoading(true);

        try {
            request("POST", "/register", { 
                username: username,
                email: email,
                password: password
            })
               .then((response) => {
                    setAuthToken(response.data.token);
                    navigate("/login");
                })
               .catch(() => {
                    navigate("/");
                });
        } catch (err) {
            setError(err.response?.data.message || "An unexpected error occurred. Please try again later.");
        } finally {
            setIsLoading(false);
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setError("");
        setIsLoading(true);

        try {
            onRegister(event, username, email, password);
        } catch (err) {
            setError(err.response?.data.message || "An unexpected error occurred. Please try again later.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="registerPage">
            <div className="formContainer">
                <form onSubmit={handleSubmit}>
                    <h1>Create an Account</h1>
                    <input
                        name="username"
                        type="text"
                        placeholder="Username"
                        id='username'
                        value={username}
                        onChange={this.onChangeHandler}
                    />
                    <input
                        name="email"
                        type="text"
                        placeholder="Email"
                        id='username'
                        value={email}
                        onChange={this.onChangeHandler}
                    />
                    <input
                        name="password"
                        type="password"
                        placeholder="Password"
                        id='password'
                        value={password}
                        onChange={this.onChangeHandler}
                    />
                    <button disabled={isLoading}>Register</button>
                    {error && <span className="error">{error}</span>}
                    <Link to="/login">Already have an account?</Link>
                </form>
            </div>
            <div className="imgContainer">
                <img src="/bg.png" alt="" />
            </div>
        </div>
    );
}

export default Register;
