import './map.scss';
import {
    MapContainer,
    TileLayer,
    Marker,
    Popup
} from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet"; // Import leaflet library

import markerIcon from './marker.png'; // Import custom marker icon

function Map() {
    const center = [27.7172, 85.324]; // Kathmandu coordinates
    const markerPosition = [27.7172, 85.324]; // Marker position

    // Create custom icon
    const customIcon = L.icon({
        iconUrl: markerIcon,
        iconSize: [32, 32], // Adjust the size of the icon as needed
        iconAnchor: [16, 32], // Adjust the anchor of the icon as needed
    });

    return (
        <MapContainer center={center} zoom={13} scrollWheelZoom={false} className="map">
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={markerPosition} icon={customIcon}>
                <Popup>
                    This is a custom marker icon.
                </Popup>
            </Marker>
        </MapContainer>
    );
}

export default Map;
